    //启用对象展开运算符
    //注意：你需要自己提供Object.assign polyfill！
    objectAssign: 'Object.assign',

    // turn off the `with` removal
    transforms: {
      stripWith: false
    }
  }
}

// webpack 2
module: {
  rules: [
    {
      test: /\.vue$/,
      loader: 'vue-loader',
      options: {
        buble: {
          // same options
        }
      }
    }
  ]
}
